package com.example.crudoperation;import java.sql.*;public class Db {   public Connection getConnection(){       try {           String driver="com.mysql.cj.jdbc.Driver";           String databaseUrl="jdbc:mysql://localhost:3306/Javafx";           String userName ="root";           String password="";           Class.forName(driver);           Connection conn=DriverManager.getConnection(databaseUrl,userName,password);           System.out.println("Database connection Successfully.....");           return conn;       }       catch (Exception e){           System.out.println(e.getMessage());       }       return null;   }}