package com.example.crudoperation;import javafx.event.ActionEvent;import javafx.fxml.FXML;import javafx.fxml.FXMLLoader;import javafx.scene.Node;import javafx.scene.Parent;import javafx.scene.Scene;import javafx.scene.control.*;import javafx.scene.input.MouseEvent;import javafx.stage.Stage;import javafx.stage.StageStyle;import java.io.IOException;import java.sql.*;public class LoginViewController {    String driver="com.mysql.cj.jdbc.Driver";    String databaseUrl="jdbc:mysql://localhost:3306/Javafx";    String userName ="root";    String password="";    Connection conn= DriverManager.getConnection(databaseUrl,userName,password);    private double xOffSet=0;    private double yOffSet=0;    @FXML    private Button btnLogin;    @FXML    private Label lblOutput;    @FXML    private PasswordField txtPassword;    @FXML    private TextField txtUsername;    public LoginViewController() throws SQLException {    }    @FXML    void handle_close(MouseEvent event) {        System.exit(0);    }    @FXML    public void login_Handle(ActionEvent event) throws SQLException, IOException {        try{            String user=txtUsername.getText();            String pass=txtPassword.getText();            if (txtUsername.getText().equals("") || txtPassword.getText().equals("")){                lblOutput.setText("please file the blanks!!");            }            else {                Statement ste=conn.createStatement();                String sql="Select * from login where username = '"+user+"' and password ='" + pass + "'" ;                ResultSet rs=ste.executeQuery(sql);                if (rs.next()){                    Parent root = FXMLLoader.load(getClass().getResource("hello-view.fxml"));                    Stage st=new Stage();                    Scene scene=new Scene(root);                    st.setScene(scene);                    st.setTitle("Student Management System");                    root.setOnMousePressed(e->{                        xOffSet=e.getSceneX();                        yOffSet=e.getSceneY();                    });                    root.setOnMouseDragged(e->{                        st.setX(e.getScreenX()-xOffSet);                        st.setY(e.getScreenY()-yOffSet);                    });                    st.initStyle(StageStyle.UNDECORATED);                    st.show();                    ((Node)(event.getSource())).getScene().getWindow().hide();                }                else {                    lblOutput.setText("invalid username or password");                }            }        }        catch (Exception ex){            Alert a=new Alert(Alert.AlertType.ERROR);            a.setTitle("Error");            a.setHeaderText(null);            a.setContentText(ex.getMessage());            a.showAndWait();            System.out.println(ex.getMessage());        }    }}