package com.example.crudoperation;public class Student {    int id;    String name;    int age;    String mobile;    String parent;    String address;    public Student( int id,String name, int age, String mobile, String parent, String address) {        this.id=id;        this.name = name;        this.age = age;        this.mobile = mobile;        this.parent = parent;        this.address = address;    }    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public int getAge() {        return age;    }    public void setAge(int age) {        this.age = age;    }    public String getMobile() {        return mobile;    }    public void setMobile(String mobile) {        this.mobile = mobile;    }    public String getParent() {        return parent;    }    public void setParent(String parent) {        this.parent = parent;    }    public String getAddress() {        return address;    }    public void setAddress(String address) {        this.address = address;    }}